mapper.select:
, @row_num totalcount, #{pageIndex} AS pageIndex, #{pageSize} AS pageSize FROM mailbox mail LEFT JOIN content co1 on mail.subject_id=co1.id LEFT JOIN content co2 on mail.content_id=co2.id INNER JOIN (select @row_num:=@row_num+1 rownum, result.id FROM ( select @row_num:=0, webmail.id from ( select * from mailbox where account=#{account}
and (status=0 or status=1)
and status=#{status}
) as webmail LEFT JOIN (select * from content WHERE status=0) as co3 ON webmail.subject_id = co3.id LEFT JOIN (select * from content WHERE status=0) as co4 ON webmail.content_id = co4.id ORDER BY
webmail.receive_time
webmail.source_id
webmail.status
webmail.receive_time
DESC , webmail.receive_time DESC ) as result) as order_result ON mail.id = order_result.id WHERE order_result.rownum >#{pageSize}*(#{pageIndex}-1) AND order_result.rownum <=#{pageSize}*#{pageIndex} ORDER BY order_result.rownum


FROM (
  select * from mailbox where id=#{mailId} and account=#{account}
) as mail
LEFT JOIN (select * from content where status=0) as co1 ON mail.subject_id = co1.id
LEFT JOIN (select * from content where status=0) as co2 ON mail.content_id = co2.id

         from (
        select * from mailbox where account=#{account}
        
            
                and (status=0 or status=1)
            
            
                and status=#{status}
            
        
        
            and receive_time >=#{startTime}
        

        ) as mail
        LEFT JOIN (select * from content WHERE status=0) as co1 ON mail.subject_id = co1.id
        LEFT JOIN (select * from content WHERE status=0) as co2 ON mail.content_id = co2.id
        ORDER BY
        
            
                mail.receive_time
            
            
                mail.source_id
            
            
                mail.status
            
            
                mail.receive_time
            
        
            DESC 
         , mail.receive_time DESC
